import React from "react";
import {
  createStyles,
  Theme,
  withStyles,
  WithStyles,
  makeStyles,
} from "@material-ui/core/styles";
import CloseIcon from "@material-ui/icons/Close";
import {
  IconButton,
  Typography,
  DialogActions,
  DialogContent,
  DialogTitle,
  Dialog,
  Button, Avatar, Grid
} from "@material-ui/core";
import { BsLink } from "react-icons/bs";
import { amber } from "@material-ui/core/colors";
import { LibraryBooksRounded, CreateRounded } from '@material-ui/icons';
const useStyles = makeStyles((theme) => ({
  root: {
    "& .MuiPaper-root": {
      background: "#ffffff94",
      backdropFilter: "blur(8px)",
      color: "#1b1a1a",
    },
  },
  avatar: {
    color: theme.palette.getContrastText(amber[500]),
    backgroundColor: amber[500],
    marginRight: 10
  },
}));
const styles = (theme: Theme) =>
  createStyles({
    root: {
      zIndex: 2,
      margin: 0,
      padding: theme.spacing(2),
      //background: '#182437',
      color: "#1b1a1a",
    },
    closeButton: {
      position: "absolute",
      right: theme.spacing(1),
      top: theme.spacing(1),
      color: theme.palette.grey[500],
    },
    subtitle: {
      color: "#1b1a1a",
    },
  });

export interface DialogTitleProps extends WithStyles<typeof styles> {
  id: string;
  children: React.ReactNode;
  onClose: () => void;
}

const MuiDialogTitle = withStyles(styles)((props: DialogTitleProps) => {
  const { children, classes, onClose, ...other } = props;
  return (
    <DialogTitle disableTypography className={classes.root} {...other}>
      <Typography variant="h6">{children}</Typography>
      {onClose ? (
        <IconButton
          aria-label="close"
          className={classes.closeButton}
          onClick={onClose}
        >
          <CloseIcon />
        </IconButton>
      ) : null}
    </DialogTitle>
  );
});

const MuiDialogContent = withStyles((theme) => ({
  root: {
    padding: theme.spacing(2),
    //background: '#182437',
    color: "#1b1a1a",
  },
}))(DialogContent);

const MuiDialogActions = withStyles((theme) => ({
  root: {
    margin: 0,
    padding: theme.spacing(1),
    //background: '#182437',
    color: "#1b1a1a",
  },
}))(DialogActions);

export interface CustomizedDialogsProps {
  open: boolean;
  setOpen: any;
  data: any;
}

export default function CustomizedDialogs({
  open,
  setOpen,
  data
}: CustomizedDialogsProps) {
  const handleClose = () => {
    setOpen(false);
  };
  const classes = useStyles();
  //  const { platformId, learningTopic, viewCount, point, satisfactionCount, code, learningObjective, link, thumbnail, courseCategoryId, name, id } = props;
  return (
    <div>
      <Dialog
        onClose={handleClose}
        fullWidth
        maxWidth="sm"
        className={classes.root}
        aria-labelledby="customized-dialog-title"
        open={open}
      >
        <MuiDialogTitle id="customized-dialog-title" onClose={handleClose}>
          <Typography variant="h6">{data.name}</Typography>
        </MuiDialogTitle>
        <MuiDialogContent dividers>
          <Grid container direction="row" justify="flex-start" alignItems="center">
            <Grid item>
              <Avatar className={classes.avatar} ><LibraryBooksRounded /></Avatar>
            </Grid>
            <Grid item>
              <h2 >เป้าหมายการเรียนรู้</h2>
            </Grid>
          </Grid>


          <Typography gutterBottom className={`color:'#90a0aa',fontWeigth:300`}>
            <div dangerouslySetInnerHTML={{ __html: data.learningTopic }} />

          </Typography>
          <Grid container direction="row" justify="flex-start" alignItems="center">
            <Grid item>
              <Avatar className={classes.avatar} ><CreateRounded /></Avatar>
            </Grid>
            <Grid item>
              <h2 >ประเด็นการเรียนรู้</h2>
            </Grid>
          </Grid>


          <Typography gutterBottom>
            <div dangerouslySetInnerHTML={{ __html: data.learningObjective }} />

          </Typography>
          <h3>กลุ่มเป้าหมาย</h3>

          <Typography gutterBottom>  <div dangerouslySetInnerHTML={{ __html: data.targetGroup }} /></Typography>
          <h3>วิธีการประเมินผล</h3>

          <Typography gutterBottom>
            <div dangerouslySetInnerHTML={{ __html: data.assessment }} />

          </Typography>
        </MuiDialogContent>
        <MuiDialogActions>
          <Button autoFocus onClick={handleClose}>
            <BsLink size={24} style={{ color: "#1b1a1a" }} />
          </Button>
        </MuiDialogActions>
      </Dialog>
    </div>
  );
}
