import React from "react";
import clsx from "clsx";
import { makeStyles } from "@material-ui/core/styles";
import {
  Grid,
  Typography,
  Box,
} from "@material-ui/core";

import Routes from "./Routes";

import Nav from "./Nav"
const drawerWidth = 240;

const useStyles = makeStyles((theme) => ({
  root: {
    display: "block",
    background: theme.palette.primary.light,
    width: "100%",
    overflowX: "hidden",
  },



  content: {
    flexGrow: 1,
    minHeight: `100vh`,

  },
  push: {
    height: "120px",
  },
  footer: {
    background: "#0f1626", //transparent
    color: "#f3f3fb",
    backdropFilter: "blur(6px)",
  },
  contentShift: {
    transition: theme.transitions.create("margin", {
      easing: theme.transitions.easing.easeOut,
    }),
    marginLeft: 0,
  },
  color: {

    color: theme.palette.secondary.light,
    fontWeight: 400,
    fontSize: 16,
    [theme.breakpoints.down("sm")]: {
      textAlign: "center",
      fontSize: 10,
    }
  },
  color1: {
    fontWeight: 400,
    fontSize: 16,
    color: theme.palette.primary.light,
    [theme.breakpoints.down("sm")]: {
      textAlign: "center",
      fontSize: 10,
    }
  },
  name: {
    fontWeight: 700,
    fontSize: 16,
    marginLeft: 10,
    whiteSpace: "nowrap",
    overflow: "hidden",
    textOverflow: "ellipsis",
    maxWidth: "75ch"
  },
  main: {
    fontWeight: 700,
    fontSize: 16,
    [theme.breakpoints.down("sm")]: {
      fontWeight: 700,
      fontSize: 12,
    },
    "&:hover": {
      color: theme.palette.secondary.main,
    },
  },
  line: {
    display: "inline-block",
    borderBottom: `3px solid ${theme.palette.secondary.main}`,
    paddingBottom: "2px",
  },
  button: {
    color: "#fdfdfd",
    "&:hover": {
      color: theme.palette.secondary.main,
    },
  },
  selected: {
    color: theme.palette.secondary.main,
  },
  selectedMain: {
    fontWeight: 700,
    fontSize: 16,
    [theme.breakpoints.down("sm")]: {
      fontWeight: 700,
      fontSize: 12,
    },
    color: theme.palette.secondary.main,
  },
}));

export default function PersistentDrawerLeft(props: any) {
  const classes = useStyles();


  return (
    <div className={classes.root}>
      <Nav />

      <Grid className={clsx(classes.content)}>
        <Routes />


      </Grid>
      <div className={classes.push} />
      <Box p={3} className={classes.footer}>
        <Grid container direction="row" justify="space-around" alignItems="center">

          <Typography
            variant="button"
            display="block"
            align="justify"
            gutterBottom
            className={classes.color1}
          >
            สำนักงานคณะกรรมการข้าราชการพลเรือน (สำนักงาน ก.พ.)
                   <Typography
              variant="button"
              display="block"
              align="justify"
              gutterBottom
              className={classes.color}
            >
              Copyright © office of the Civil Service Commission (OCSC) 2020
          </Typography>
          </Typography>
          <Typography

            display="block"
            align="justify"

            className={classes.color1}
          >
            47/111 หมู่ 4 ถนนติวานนท์ ตำบลตลาดขวัญ อำเภอเมือง จังหวัดนนทบุรี 11000
                  <Typography

              display="block"
              align="justify"

              className={classes.color}
            >
              E-mail : ocsc.hrd@gmail.com
          </Typography>

            <Typography

              display="block"
              align="justify"
              gutterBottom
              className={classes.color1}
            >
              โทร. 02-547-1795 , 02-547-1807 (ภายในเวลาราชการ)
          </Typography>



          </Typography>
        </Grid>
      </Box>
    </div>
  );
}
