import React, { useEffect, Suspense, lazy } from "react";
import { Grid, Divider, Container, CircularProgress } from "@material-ui/core";


import { makeStyles } from "@material-ui/core/styles";

import { useLocation } from "react-router-dom"
import { parse } from "query-string"
import { useDispatch, useSelector } from 'react-redux'
import * as actions from "../../actions"
import searchNotfound from "assets/gif/somethingbroken.gif"
import Search from "../Search"
const CourseDetail = lazy(() => import('../Course/CourseDetails'));
const Header = lazy(() => import('../../share/Header'));
const SelectCategory = lazy(() => import('../../share/SelectCategory'));


const useStyles = makeStyles((theme) => ({
    root: {
        paddingTop: "20px"
    },
    content: {
        paddingRight: "8vw",
        paddingLeft: "8vw",
    },
    divider: {
        marginBottom: 20
    },
    circular: {
        marginTop: 20
    }

}));
export default function GroupCourse(props: any) {
    const dispatch = useDispatch();
    const { course, isLoadingCourse } = useSelector((state: any) => state.course);
    const classes = useStyles()
    const { search } = useLocation()
    const { q } = parse(search)
    useEffect(() => {
        const action = actions.loadCourse(q)
        dispatch(action)
        // eslint-disable-next-line
    }, [q])



    const renderLoader = () =>
        <div></div>

    return (
        <Suspense fallback={renderLoader()}>
            <Header text={"ค้นหารายวิชา"} />


            <div className={classes.root}>

                <Search />

                <Grid container className={classes.content} direction="row" alignItems="center" justify={"space-between"} >

                    <h2>ค้นหา {q}</h2>
                    <SelectCategory />

                </Grid>
                <Divider className={classes.divider} />

                <Container maxWidth="lg">
                    <Grid
                        container
                        direction="row"
                        alignItems="center"
                        justify={isLoadingCourse ? "center" : "flex-start"}
                        spacing={3}
                    >

                        {isLoadingCourse && <CircularProgress color="secondary" className={classes.circular} />}
                        {course.length === 0 && !isLoadingCourse &&

                            <Grid
                                container
                                direction="row"
                                alignItems="center"
                                justify={"center"}
                            >
                                <h2>ขออภัยค่ะ หารายวิชาไม่พบ {q}</h2>
                                <img src={searchNotfound} alt="" width="400px" height="400px" />

                            </Grid>
                        }
                        {course.map((item: any, index: number) => (
                            <Grid item xs={12} sm={6} md={4} lg={3} xl={3}>
                                <CourseDetail
                                    key={index}
                                    id={item.id}
                                    learningTopic={item.learningTopic}
                                    learningObjective={item.learningObjective}
                                    courseCategoryId={item.courseCategoryId}
                                    thumbnail={item.thumbnail}
                                    viewCount={item.viewCount}
                                    assessment={item.assessment}
                                    targetGroup={item.targetGroup}
                                    point={(item.satisfactionSum) / item.satisfactionCount}
                                    satisfactionCount={item.satisfactionCount}
                                    link={item.link}
                                    code={item.code}
                                    name={item.name}
                                    platformId={item.platformId}
                                />
                            </Grid>
                        ))
                        }
                    </Grid>
                </Container>
            </div>

        </Suspense>
    );
}
