import Avatar from "@material-ui/core/Avatar";
import amber from "@material-ui/core/colors/amber";
import Divider from "@material-ui/core/Divider";
import FormControl from "@material-ui/core/FormControl";
import FormControlLabel from "@material-ui/core/FormControlLabel";
import Grid from "@material-ui/core/Grid";
import Radio from "@material-ui/core/Radio";
import RadioGroup from "@material-ui/core/RadioGroup";
import { createStyles, makeStyles, Theme } from "@material-ui/core/styles";
import Typography from "@material-ui/core/Typography";
import React from "react";


const useStyles = makeStyles((theme: Theme) =>
    createStyles({
        root: {
            marginTop: 10
        },
        answer: {
          textDecoration:"underline"  
        },
        amber: {
            color: theme.palette.getContrastText(amber[500]),
            backgroundColor: amber[500],
        },
        divider: {
            marginTop: theme.spacing(2),
        },
        container: {
            padding: theme.spacing(1.5),
        },
    })
);

export default function ItemComponent({
    id,
    question,
    answer,
}: {
    id: number,
    question: string,
    answer: string,
}) {
    const classes = useStyles();

    return (
        <React.Fragment>
            <Grid container direction="row" alignItems="center" spacing={2} className={classes.root}>
                <Grid item>
                    <Avatar className={classes.amber}>{id}</Avatar>
                </Grid>
                <Grid item>
                    <Typography
                        component="h1"
                        variant="h6"
                        align="center"
                        style={{ fontSize: "1rem", fontWeight: 600 }}
                    >
                        {question}?
          </Typography>
                </Grid>
            </Grid>
            <Divider className={classes.divider} />
            <form>
                <FormControl component="fieldset">
                    <RadioGroup aria-label={question} name={question}>
                        <Grid className={classes.container} container spacing={0}>

                            <Grid item xs={12} className={classes.answer}>
                                คำตอบ

                            </Grid>
                            <Grid item xs={12}>
                          <  div dangerouslySetInnerHTML={{ __html:` ${answer}` }} />

                            </Grid>

                        </Grid>
                    </RadioGroup>
                </FormControl>
            </form>
        </React.Fragment>
    );
}
