import React, { useEffect } from "react";
import Type1 from "./Type1";
import Type2 from "./Type2";
import Type3 from "./Type3";
import Type4 from "./Type4";
import Type5 from "./Type5";
import Detail123 from "./Detail123";

import {
  Typography,
  TextField,
  MenuItem,
  CssBaseline,
} from "@material-ui/core";
import { makeStyles } from "@material-ui/core/styles";
import { Controller } from "react-hook-form";

import * as actions from "modules/infomation/actions";
import { useDispatch, useSelector } from "react-redux";

const useStyles = makeStyles((theme) => ({
  form: {
    marginTop: theme.spacing(4),
    padding: theme.spacing(2),
    width: "100%", // Fix IE 11 issue.
  },
  input: {
    color: "#0f1626",
    fontWeight: 600,
    "& .MuiInputBase-root.MuiOutlineInput-root": {
      color: "#45A29E",
      borderColor: "#757575",
      fontWeight: 600,
    },
  },
  selectInput: {
    color: "#757575",
    fontWeight: 600,
    "& .MuiInputBase-root.MuiOutlineInput-root": {
      color: "#45A29E",
      borderColor: "#757575",
      fontWeight: 600,
    },
  },
  textfield: {
    marginTop: 10,
    "& .MuiFormHelperText-root.Mui-error ": {
      color: "ff533d",
      fontWeight: 600,
      borderWidth: "1px",
    },
    "& .MuiInput-underline.Mui-error:after": {
      borderColor: "ff533d",
      borderWidth: "1px",
    },
    "& label.MuiFormLabel-root": {
      fontWeight: 600,
      "&:after .Mui-error": {
        borderColor: "#ff533d",
        borderWidth: "1px",
      },
    },
    "& label.Mui-focused": {
      color: "#132740",
    },
    "& .MuiInput-underline:after": {
      borderBottomColor: theme.palette.secondary.main,
    },
    "& .MuiOutlinedInput-root": {
      "& fieldset": {
        borderColor: theme.palette.secondary.main,
        borderWidth: "1px",
      },
      "&:hover fieldset": {
        borderColor: "#a8c6ff",
        borderWidth: "3px",
      },
      "&.Mui-focused fieldset": {
        borderColor: "#a8c6ff",
        borderWidth: "3px",
      },
      "&.Mui-error .MuiOutlinedInput-notchedOutline": {
        borderColor: "#ff533d",
        borderWidth: "1px",
      },
    },
  },
}));

export default function SignIn(props: any) {
  const { formProps } = props;
  const classes = useStyles();

  const dispatch = useDispatch();

  useEffect(() => {
    const action = actions.loadUserTypes();
    dispatch(action);
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  const { userTypes } = useSelector((state: any) => state.infomation);

  function renderTypeProps() {
    const value = formProps.getValues("usertypeid");
    switch (value) {
      case 1:
        return (
          <div>
            <Type1 formProps={formProps} name={"ข้าราชการพลเรือน"} />
            <Detail123 formProps={formProps} />
          </div>
        );
      case 2:
        return (
          <div>
            <Type2 formProps={formProps} name={"ข้าราชการประเภทอื่น"} />
            <Detail123 formProps={formProps} />
          </div>
        );
      case 3:
        return (
          <div>
            <Type3 formProps={formProps} name={"เจ้าหน้าที่ของรัฐในส่วนราชการต่าง ๆ"} />
            <Detail123 formProps={formProps} />
          </div>
        );
      case 5:
        return <Type4 formProps={formProps} name={"บุคคลทั่วไป"} />;
      case 4:
        return <Type5 formProps={formProps} name={"พนักงานรัฐวิสากิจ"} />;
    }
  }

  return (
    <>
      <CssBaseline />
      <form className={classes.form} noValidate>
        <Typography component="h1" variant="h5" align="center">
          ข้อมูลการทำงาน
        </Typography>
        <Controller
          as={
            <TextField
              variant="outlined"
              fullWidth
              className={classes.textfield}
              label="ประเภทข้าราชการ/เจ้าหน้าที่"
              InputLabelProps={{
                className: classes.selectInput,
              }}
              select
              helperText={
                formProps.errors.usertypeid &&
                formProps.errors.usertypeid.message
              }
              error={!!formProps.errors.usertypeid}
            >


              {userTypes.map((userType: any, index: number) =>
                (<MenuItem value={userType.id}>{userType.name}</MenuItem>)
              )}



            </TextField>
          }
          name="usertypeid"
          rules={{ required: "กรุณาเลือกประเภท" }}
          control={formProps.control}

        />

        {renderTypeProps()}
      </form>
    </>
  );
}
